/*
Five of a kind = 0
Four of a kind = 1
Full house = 2
Three of a kind = 3
Two Pair = 4
One Pair = 5
High Card = 6
*/
short RelativeStrength(string card)
{
    Dictionary<char, short> values = new Dictionary<char, short>();
    for (int i = 0; i < 5; i++)
    {
        if (values.ContainsKey(card[i]))
        {
            values[card[i]]++;
        }
        else
        {
            values[card[i]] = 1;
        }
    }
    int numberOfKeys = values.Count;
    switch (values.Count)
    {
        case 1:
            return 0;
        case 2:
            if (values.ElementAt(0).Value==4|| values.ElementAt(0).Value == 1)//four of a kind
            {
                return 1;
            }
            return 2;//full house
        case 3:
            for (int i = 0; i < 3; i++)
            {
                if (values.ElementAt(i).Value==3)
                {
                    return 3;
                }
            }
            return 4;
        case 4:
            return 5;
        case 5:
            return 6;
        default:
            throw new Exception("Valami hiba történt a kártya erősségének kiszámolásakor");
            return -1;
    }

}
bool StrongerThan(hand card,hand cont)//checks if card is stronger than contender
{
    if (card.relativeStrength<cont.relativeStrength)
    {
        return true;
    }
    else if (cont.relativeStrength<card.relativeStrength)
    {
        return false;
    }
    for (int i = 0; i < 5; i++)
    {
        if (!(card.cards[i] == cont.cards[i]))
        {
            if (cardValues.IndexOf(card.cards[i]) > cardValues.IndexOf(cont.cards[i]))
            {
                return true;
            }
            else
            {
                return false;
            }
        }
    }
    return false;
}
class hand
{
    public string cards;
    public int bid;
    public short relativeStrength;

    public hand(string cards, string bid, short relativeStrength)
    {
        this.cards = cards;
        this.bid = int.Parse(bid);
        this.relativeStrength = relativeStrength;
    }
}